```typescript
// types/roles.ts
export type UserRole = 'admin' | 'sales_rep' | 'advocate'

export interface Permission {
  action: string
  resource: string
}

export interface RolePermissions {
  [role: string]: Permission[]
}

// hooks/useRBAC.ts
import { useAuth } from './useAuth'
import { useState, useEffect } from 'react'
import { supabase } from '../lib/supabaseClient'
import { UserRole, Permission, RolePermissions } from '../types/roles'

const defaultPermissions: RolePermissions = {
  admin: [
    { action: '*', resource: '*' }
  ],
  sales_rep: [
    { action: 'read', resource: 'advocates' },
    { action: 'create', resource: 'calls' },
    { action: '*', resource: 'opportunities' }
  ],
  advocate: [
    { action: 'read', resource: 'calls' },
    { action: 'update', resource: 'profile' },
    { action: 'update', resource: 'availability' }
  ]
}

export const useRBAC = () => {
  const { user } = useAuth()
  const [userRole, setUserRole] = useState<UserRole | null>(null)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    const fetchUserRole = async () => {
      if (!user) {
        setUserRole(null)
        setLoading(false)
        return
      }

      const { data, error } = await supabase
        .from('user_roles')
        .select('role')
        .eq('user_id', user.id)
        .single()

      if (error || !data) {
        console.error('Error fetching user role:', error)
        setUserRole(null)
      } else {
        setUserRole(data.role as UserRole)
      }

      setLoading(false)
    }

    fetchUserRole()
  }, [user])

  const hasPermission = (action: string, resource: string): boolean => {
    if (!userRole) return false

    const permissions = defaultPermissions[userRole] || []

    return permissions.some(permission => 
      (permission.action === '*' || permission.action === action) &&
      (permission.resource === '*' || permission.resource === resource)
    )
  }

  return { userRole, loading, hasPermission }
}

// components/auth/PermissionGate.tsx
import { ReactNode } from 'react'
import { useRBAC } from '../../hooks/useRBAC'

type PermissionGateProps = {
  action: string
  resource: string
  children: ReactNode
  fallback?: ReactNode
}

export const PermissionGate = ({
  action,
  resource,
  children,
  fallback = null
}: PermissionGateProps) => {
  const { hasPermission, loading } = useRBAC()

  if (loading) return null

  return hasPermission(action, resource) ? <>{children}</> : <>{fallback}</>
}
```

