// services/userService.ts
import { supabase } from '../lib/supabaseClient'
import { UserProfile } from '../types/user'

export const getUserProfile = async (userId: string): Promise<UserProfile | null> => {
  const { data, error } = await supabase
    .from('profiles')
    .select('*')
    .eq('user_id', userId)
    .single()

  if (error) {
    console.error('Error fetching user profile:', error)
    return null
  }

  return data as UserProfile
}

export const updateUserProfile = async (profile: Partial<UserProfile>): Promise<{ data: UserProfile | null, error: any }> => {
  const { data, error } = await supabase
    .from('profiles')
    .update({
      ...profile,
      updated_at: new Date().toISOString()
    })
    .eq('user_id', profile.user_id)
    .select()
    .single()

  return { data: data as UserProfile, error }
}

export const uploadProfileImage = async (userId: string, file: File): Promise<{ url: string | null, error: any }> => {
  const fileExt = file.name.split('.').pop()
  const fileName = `${userId}-${Math.random().toString(36).substring(2)}.${fileExt}`
  const filePath = `avatars/${fileName}`

  const { error: uploadError } = await supabase.storage
    .from('profiles')
    .upload(filePath, file)

  if (uploadError) {
    return { url: null, error: uploadError }
  }

  const { data } = supabase.storage.from('profiles').getPublicUrl(filePath)

  // Update profile with new avatar URL
  await updateUserProfile({
    user_id: userId,
    avatar_url: data.publicUrl
  })

  return { url: data.publicUrl, error: null }
}
