// hooks/useCompany.ts
import { createContext, useContext, useState, useEffect } from 'react'
import { supabase } from '../lib/supabaseClient'
import { useAuth } from './useAuth'

interface Company {
  id: string
  name: string
  domain: string
  logo_url?: string
  settings?: Record<string, any>
}

type CompanyContextType = {
  company: Company | null
  loading: boolean
  error: any
}

const CompanyContext = createContext<CompanyContextType | undefined>(undefined)

export const CompanyProvider = ({ children }) => {
  const { user } = useAuth()
  const [company, setCompany] = useState<Company | null>(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<any>(null)

  useEffect(() => {
    const fetchCompany = async () => {
      if (!user) {
        setCompany(null)
        setLoading(false)
        return
      }

      try {
        // First get the user's company_id from their profile
        const { data: profileData, error: profileError } = await supabase
          .from('profiles')
          .select('company_id')
          .eq('user_id', user.id)
          .single()

        if (profileError) throw profileError

        if (profileData?.company_id) {
          // Then fetch the company details
          const { data: companyData, error: companyError } = await supabase
            .from('companies')
            .select('*')
            .eq('id', profileData.company_id)
            .single()

          if (companyError) throw companyError

          setCompany(companyData as Company)
        }
      } catch (err) {
        console.error('Error fetching company:', err)
        setError(err)
      } finally {
        setLoading(false)
      }
    }

    fetchCompany()
  }, [user])

  return (
    <CompanyContext.Provider value={{ company, loading, error }}>
      {children}
    </CompanyContext.Provider>
  )
}

export const useCompany = () => {
  const context = useContext(CompanyContext)
  if (context === undefined) {
    throw new Error('useCompany must be used within a CompanyProvider')
  }
  return context
}

// middleware/withTenantIsolation.ts
import { NextApiRequest, NextApiResponse } from 'next'
import { supabase } from '../lib/supabaseClient'

export const withTenantIsolation = (handler: any) => {
  return async (req: NextApiRequest, res: NextApiResponse) => {
    // Get the authorization header
    const authHeader = req.headers.authorization

    if (!authHeader) {
      return res.status(401).json({ error: 'Missing authorization header' })
    }

    // Extract the token
    const token = authHeader.split(' ')[1]

    // Verify the token and get the user
    const { data: { user }, error } = await supabase.auth.getUser(token)

    if (error || !user) {
      return res.status(401).json({ error: 'Invalid or expired token' })
    }

    // Get the user's company_id
    const { data: profile } = await supabase
      .from('profiles')
      .select('company_id')
      .eq('user_id', user.id)
      .single()

    if (!profile?.company_id) {
      return res.status(403).json({ error: 'User not associated with a company' })
    }

    // Add company_id to the request context
    req.companyId = profile.company_id
    req.userId = user.id

    // Call the original handler
    return handler(req, res)
  }
}
```
