ALTER TABLE profiles ENABLE ROW LEVEL SECURITY;
ALTER TABLE companies ENABLE ROW LEVEL SECURITY;
ALTER TABLE advocates ENABLE ROW LEVEL SECURITY;

-- Create policies for profiles table
CREATE POLICY "Users can view their own profile"
  ON profiles FOR SELECT
  USING (auth.uid() = user_id);

CREATE POLICY "Users can update their own profile"
  ON profiles FOR UPDATE
  USING (auth.uid() = user_id);

-- Create policies for company-based isolation
CREATE POLICY "Users can view data from their company"
  ON advocates FOR SELECT
  USING (
    company_id IN (
      SELECT company_id FROM profiles
      WHERE user_id = auth.uid()
    )
  );

CREATE POLICY "Users can insert data for their company"
  ON advocates FOR INSERT
  WITH CHECK (
    company_id IN (
      SELECT company_id FROM profiles
      WHERE user_id = auth.uid()
    )
  );

CREATE POLICY "Users can update data for their company"
  ON advocates FOR UPDATE
  USING (
    company_id IN (
      SELECT company_id FROM profiles
      WHERE user_id = auth.uid()
    )
  );
