Example code:
```typescript
// services/availability/availabilityCalculator.ts
import { AvailabilitySlot } from '../calendarService';

interface CalendarEvent {
  start: Date;
  end: Date;
}

interface WorkingHours {
  daysOfWeek: number[]; // 0 = Sunday, 6 = Saturday
  startTime: string; // Format: "HH:MM"
  endTime: string; // Format: "HH:MM"
  timeZone: string;
}

export const mergeEvents = (eventSets: CalendarEvent[][]): CalendarEvent[] => {
  // Flatten and sort all events
  const allEvents = eventSets.flat().sort((a, b) => a.start.getTime() - b.start.getTime());

  // Merge overlapping events
  const mergedEvents: CalendarEvent[] = [];

  for (const event of allEvents) {
    const lastEvent = mergedEvents[mergedEvents.length - 1];

    if (!lastEvent || event.start > lastEvent.end) {
      mergedEvents.push(event);
    } else if (event.end > lastEvent.end) {
      lastEvent.end = event.end;
    }
  }

  return mergedEvents;
};

export const calculateAvailability = (
  startDate: Date,
  endDate: Date,
  events: CalendarEvent[],
  workingHours: WorkingHours,
  bufferMinutes: number = 0,
  minimumDurationMinutes: number = 15
): AvailabilitySlot[] => {
  const availableSlots: AvailabilitySlot[] = [];
  const mergedEvents = [...events];

  // Add working hours constraints as events
  // ...

  // Sort events chronologically
  mergedEvents.sort((a, b) => a.start.getTime() - b.start.getTime());

  // Find gaps between events
  let currentTime = new Date(startDate);

  for (const event of mergedEvents) {
    // Add buffer to current time
    const bufferTime = new Date(currentTime);
    bufferTime.setMinutes(bufferTime.getMinutes() + bufferMinutes);

    // If there's a gap between current time (with buffer) and next event
    if (bufferTime < event.start) {
      // Check if gap meets minimum duration
      const gapDuration = event.start.getTime() - bufferTime.getTime();
      if (gapDuration >= minimumDurationMinutes * 60 * 1000) {
        availableSlots.push({
          start: new Date(bufferTime),
          end: new Date(event.start)
        });
      }
    }

    // Move current time to end of event plus buffer
    currentTime = new Date(event.end);
    currentTime.setMinutes(currentTime.getMinutes() + bufferMinutes);
  }

  // Check for availability after last event
  if (currentTime < endDate) {
    availableSlots.push({
      start: new Date(currentTime),
      end: new Date(endDate)
    });
  }

  return availableSlots;
};
```

