// services/notifications/calendarNotifications.ts
import { Meeting } from '../calendarService';
import { sendEmail } from './emailService';
import { sendSms } from './smsService';
import { createInAppNotification } from './inAppNotificationService';

export enum NotificationType {
  BOOKING_CONFIRMATION = 'booking_confirmation',
  MEETING_REMINDER = 'meeting_reminder',
  MEETING_RESCHEDULED = 'meeting_rescheduled',
  MEETING_CANCELLED = 'meeting_cancelled'
}

export interface NotificationPreferences {
  email: boolean;
  sms: boolean;
  inApp: boolean;
  reminderTimes: number[]; // Minutes before meeting
}

export const sendMeetingNotification = async (
  type: NotificationType,
  meeting: Meeting,
  recipients: Array<{
    email: string;
    phone?: string;
    userId?: string;
    preferences: NotificationPreferences;
  }>
): Promise<void> => {
  const notifications = [];

  for (const recipient of recipients) {
    // Send email notification
    if (recipient.preferences.email) {
      notifications.push(sendEmail({
        to: recipient.email,
        templateId: getEmailTemplateForType(type),
        data: {
          meeting,
          recipientEmail: recipient.email
        }
      }));
    }

    // Send SMS notification
    if (recipient.preferences.sms && recipient.phone) {
      notifications.push(sendSms({
        to: recipient.phone,
        templateId: getSmsTemplateForType(type),
        data: {
          meeting
        }
      }));
    }

    // Create in-app notification
    if (recipient.preferences.inApp && recipient.userId) {
      notifications.push(createInAppNotification({
        userId: recipient.userId,
        type,
        data: {
          meeting
        }
      }));
    }
  }

  await Promise.all(notifications);
};

export const scheduleMeetingReminders = async (
  meeting: Meeting,
  recipients: Array<{
    email: string;
    phone?: string;
    userId?: string;
    preferences: NotificationPreferences;
  }>
): Promise<void> => {
  for (const recipient of recipients) {
    for (const reminderTime of recipient.preferences.reminderTimes) {
      const reminderDate = new Date(meeting.startTime);
      reminderDate.setMinutes(reminderDate.getMinutes() - reminderTime);

      // Schedule reminder
      await scheduleNotification({
        type: NotificationType.MEETING_REMINDER,
        meeting,
        recipient,
        scheduledTime: reminderDate
      });
    }
  }
};
