// components/DateTimePicker.tsx
import React, { useState } from 'react';
import { format, addMinutes } from 'date-fns';
import { Calendar } from './Calendar';
import { TimeSelector } from './TimeSelector';
import { TimeZoneSelector } from './TimeZoneSelector';
import { DurationSelector } from './DurationSelector';

interface DateTimePickerProps {
  initialDate?: Date;
  minDate?: Date;
  maxDate?: Date;
  timeZone?: string;
  availableTimeSlots?: Array<{
    start: Date;
    end: Date;
  }>;
  onChange: (selection: {
    startTime: Date;
    endTime: Date;
    timeZone: string;
  }) => void;
}

export const DateTimePicker: React.FC<DateTimePickerProps> = ({
  initialDate = new Date(),
  minDate,
  maxDate,
  timeZone = Intl.DateTimeFormat().resolvedOptions().timeZone,
  availableTimeSlots = [],
  onChange
}) => {
  const [selectedDate, setSelectedDate] = useState<Date>(initialDate);
  const [selectedTime, setSelectedTime] = useState<string>(format(initialDate, 'HH:mm'));
  const [selectedTimeZone, setSelectedTimeZone] = useState<string>(timeZone);
  const [duration, setDuration] = useState<number>(30); // minutes

  // Get available time slots for the selected date
  const availableTimesForDate = availableTimeSlots.filter(slot => {
    const slotDate = new Date(slot.start);
    return (
      slotDate.getFullYear() === selectedDate.getFullYear() &&
      slotDate.getMonth() === selectedDate.getMonth() &&
      slotDate.getDate() === selectedDate.getDate()
    );
  });

  // Handle date change
  const handleDateChange = (date: Date) => {
    setSelectedDate(date);

    // If there are available slots for this date, select the first one
    if (availableTimesForDate.length > 0) {
      setSelectedTime(format(availableTimesForDate[0].start, 'HH:mm'));
    }
  };

  // Handle time change
  const handleTimeChange = (time: string) => {
    setSelectedTime(time);
    updateSelectedDateTime(selectedDate, time, selectedTimeZone, duration);
  };

  // Handle time zone change
  const handleTimeZoneChange = (tz: string) => {
    setSelectedTimeZone(tz);
    updateSelectedDateTime(selectedDate, selectedTime, tz, duration);
  };

  // Handle duration change
  const handleDurationChange = (mins: number) => {
    setDuration(mins);
    updateSelectedDateTime(selectedDate, selectedTime, selectedTimeZone, mins);
  };

  // Update the selected date/time and call onChange
  const updateSelectedDateTime = (date: Date, time: string, tz: string, durationMins: number) => {
    const [hours, minutes] = time.split(':').map(Number);

    const startTime = new Date(date);
    startTime.setHours(hours, minutes, 0, 0);

    const endTime = addMinutes(startTime, durationMins);

    onChange({
      startTime,
      endTime,
      timeZone: tz
    });
  };

  return (
    <div className="date-time-picker">
      <Calendar
        selectedDate={selectedDate}
        minDate={minDate}
        maxDate={maxDate}
        availableDates={availableTimeSlots.map(slot => new Date(slot.start))}
        onChange={handleDateChange}
      />

      <TimeSelector
        selectedTime={selectedTime}
        availableSlots={availableTimesForDate}
        onChange={handleTimeChange}
      />

      <DurationSelector
        duration={duration}
        onChange={handleDurationChange}
        options={[15, 30, 45, 60, 90]}
      />

      <TimeZoneSelector
        selectedTimeZone={selectedTimeZone}
        onChange={handleTimeZoneChange}
      />
    </div>
  );
};
