// components/AvailabilityDisplay.tsx
import React, { useState, useEffect } from 'react';
import { format, addDays, isSameDay } from 'date-fns';
import { AvailabilitySlot } from '../services/calendarService';

interface AvailabilityDisplayProps {
  userId: string;
  startDate: Date;
  numberOfDays: number;
  onSlotSelect: (slot: {
    start: Date;
    end: Date;
  }) => void;
  slotDuration: number; // minutes
  workingHours: {
    start: string; // Format: "HH:MM"
    end: string; // Format: "HH:MM"
  };
}

export const AvailabilityDisplay: React.FC<AvailabilityDisplayProps> = ({
  userId,
  startDate,
  numberOfDays,
  onSlotSelect,
  slotDuration,
  workingHours
}) => {
  const [availabilitySlots, setAvailabilitySlots] = useState<AvailabilitySlot[]>([]);
  const [isLoading, setIsLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  // Calculate end date
  const endDate = addDays(startDate, numberOfDays);

  // Fetch availability data
  const fetchAvailability = async () => {
    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch(`/api/availability?userId=${userId}&startDate=${startDate.toISOString()}&endDate=${endDate.toISOString()}`);

      if (!response.ok) {
        throw new Error('Failed to fetch availability');
      }

      const data = await response.json();
      setAvailabilitySlots(data.availabilitySlots.map((slot: any) => ({
        start: new Date(slot.start),
        end: new Date(slot.end)
      })));
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchAvailability();
  }, [userId, startDate, numberOfDays]);

  // Generate time slots for display
  const generateTimeSlots = () => {
    const days = [];

    for (let i = 0; i < numberOfDays; i++) {
      const currentDate = addDays(startDate, i);
      const daySlots = [];

      // Parse working hours
      const [startHour, startMinute] = workingHours.start.split(':').map(Number);
      const [endHour, endMinute] = workingHours.end.split(':').map(Number);

      // Create slots within working hours
      const slotStartTime = new Date(currentDate);
      slotStartTime.setHours(startHour, startMinute, 0, 0);

      const dayEndTime = new Date(currentDate);
      dayEndTime.setHours(endHour, endMinute, 0, 0);

      while (slotStartTime < dayEndTime) {
        const slotEndTime = new Date(slotStartTime);
        slotEndTime.setMinutes(slotEndTime.getMinutes() + slotDuration);

        if (slotEndTime > dayEndTime) {
          break;
        }

        // Check if slot is available
        const isAvailable = availabilitySlots.some(availableSlot => {
          return (
            slotStartTime >= availableSlot.start &&
            slotEndTime <= availableSlot.end
          );
        });

        daySlots.push({
          start: new Date(slotStartTime),
          end: new Date(slotEndTime),
          isAvailable
        });

        // Move to next slot
        slotStartTime.setMinutes(slotStartTime.getMinutes() + slotDuration);
      }

      days.push({
        date: currentDate,
        slots: daySlots
      });
    }

    return days;
  };

  const timeSlots = generateTimeSlots();

  // Handle slot selection
  const handleSlotClick = (slot: {
    start: Date;
    end: Date;
    isAvailable: boolean;
  }) => {
    if (slot.isAvailable) {
      onSlotSelect({
        start: slot.start,
        end: slot.end
      });
    }
  };

  if (isLoading) {
    return <div className="loading">Loading availability...</div>;
  }

  if (error) {
    return (
      <div className="error">
        <p>{error}</p>
        <button onClick={fetchAvailability}>Retry</button>
      </div>
    );
  }

  return (
    <div className="availability-display">
      <div className="days-container">
        {timeSlots.map((day, dayIndex) => (
          <div key={dayIndex} className="day-column">
            <div className="day-header">
              {format(day.date, 'EEE, MMM d')}
            </div>
            <div className="slots-container">
              {day.slots.map((slot, slotIndex) => (
                <div
                  key={slotIndex}
                  className={`time-slot ${slot.isAvailable ? 'available' : 'busy'}`}
                  onClick={() => handleSlotClick(slot)}
                >
                  {format(slot.start, 'h:mm a')}
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
      <button className="refresh-button" onClick={fetchAvailability}>
        Refresh Availability
      </button>
    </div>
  );
};
