// components/RescheduleMeeting.tsx
import React, { useState, useEffect } from 'react';
import { Meeting } from '../services/calendarService';
import { DateTimePicker } from './DateTimePicker';
import { AvailabilityDisplay } from './AvailabilityDisplay';
import { BookingConfirmation } from './BookingConfirmation';

enum RescheduleStep {
  LOADING,
  CURRENT_DETAILS,
  SELECT_NEW_TIME,
  CONFIRMATION
}

interface RescheduleMeetingProps {
  meetingId: string;
  userId: string;
  onComplete: () => void;
  onCancel: () => void;
}

export const RescheduleMeeting: React.FC<RescheduleMeetingProps> = ({
  meetingId,
  userId,
  onComplete,
  onCancel
}) => {
  const [currentStep, setCurrentStep] = useState<RescheduleStep>(RescheduleStep.LOADING);
  const [currentMeeting, setCurrentMeeting] = useState<Meeting | null>(null);
  const [newMeeting, setNewMeeting] = useState<Meeting | null>(null);
  const [selectedTimeSlot, setSelectedTimeSlot] = useState<{
    startTime: Date;
    endTime: Date;
    timeZone: string;
  } | null>(null);
  const [availabilitySlots, setAvailabilitySlots] = useState<Array<{
    start: Date;
    end: Date;
  }>>([]);
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);

  // Fetch current meeting details
  useEffect(() => {
    const fetchMeeting = async () => {
      try {
        const response = await fetch(`/api/meetings/${meetingId}`);

        if (!response.ok) {
          throw new Error('Failed to fetch meeting details');
        }

        const data = await response.json();
        setCurrentMeeting({
          ...data,
          startTime: new Date(data.startTime),
          endTime: new Date(data.endTime)
        });

        setCurrentStep(RescheduleStep.CURRENT_DETAILS);
      } catch (err) {
        setError(err.message);
      }
    };

    fetchMeeting();
  }, [meetingId]);

  // Fetch availability for new time selection
  const fetchAvailability = async (startDate: Date, days: number) => {
    setIsLoading(true);

    try {
      const endDate = new Date(startDate);
      endDate.setDate(endDate.getDate() + days);

      const response = await fetch(`/api/availability?userId=${userId}&startDate=${startDate.toISOString()}&endDate=${endDate.toISOString()}`);

      if (!response.ok) {
        throw new Error('Failed to fetch availability');
      }

      const data = await response.json();
      setAvailabilitySlots(data.availabilitySlots.map((slot: any) => ({
        start: new Date(slot.start),
        end: new Date(slot.end)
      })));
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  // Handle time slot selection
  const handleTimeSlotSelect = (slot: {
    start: Date;
    end: Date;
  }) => {
    setSelectedTimeSlot({
      startTime: slot.start,
      endTime: slot.end,
      timeZone: Intl.DateTimeFormat().resolvedOptions().timeZone
    });
  };

  // Handle date/time selection from picker
  const handleDateTimeChange = (selection: {
    startTime: Date;
    endTime: Date;
    timeZone: string;
  }) => {
    setSelectedTimeSlot(selection);
  };

  // Proceed to new time selection
  const proceedToTimeSelection = () => {
    setCurrentStep(RescheduleStep.SELECT_NEW_TIME);

    // Start with current date for availability
    const startDate = new Date();
    fetchAvailability(startDate, 14); // Fetch 2 weeks of availability
  };

  // Reschedule the meeting
  const handleReschedule = async () => {
    if (!currentMeeting || !selectedTimeSlot) return;

    setIsLoading(true);

    try {
      // Call API to reschedule meeting
      const response = await fetch(`/api/meetings/${meetingId}/reschedule`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          startTime: selectedTimeSlot.startTime.toISOString(),
          endTime: selectedTimeSlot.endTime.toISOString(),
          timeZone: selectedTimeSlot.timeZone
        })
      });

      if (!response.ok) {
        throw new Error('Failed to reschedule meeting');
      }

      const data = await response.json();
      setNewMeeting({
        ...data,
        startTime: new Date(data.startTime),
        endTime: new Date(data.endTime)
      });

      setCurrentStep(RescheduleStep.CONFIRMATION);
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  // Render based on current step
  const renderContent = () => {
    switch (currentStep) {
      case RescheduleStep.LOADING:
        return <div className="loading">Loading meeting details...</div>;

      case RescheduleStep.CURRENT_DETAILS:
        if (!currentMeeting) return null;

        return (
          <div className="current-meeting">
            <h2>Current Meeting Details</h2>
            <BookingConfirmation
              meeting={currentMeeting}
              showAddToCalendarOptions={false}
            />
            <div className="action-buttons">
              <button
                className="primary-button"
                onClick={proceedToTimeSelection}
              >
                Select New Time
              </button>
              <button
                className="secondary-button"
                onClick={onCancel}
              >
                Cancel
              </button>
            </div>
          </div>
        );

      case RescheduleStep.SELECT_NEW_TIME:
        return (
          <div className="select-new-time">
            <h2>Select New Meeting Time</h2>
            <div className="time-selection-container">
              <div className="availability-section">
                <h3>Available Time Slots</h3>
                <AvailabilityDisplay
                  userId={userId}
                  startDate={new Date()}
                  numberOfDays={7}
                  onSlotSelect={handleTimeSlotSelect}
                  slotDuration={30}
                  workingHours={{ start: '09:00', end: '17:00' }}
                />
              </div>
              <div className="date-time-picker-section">
                <h3>Custom Date/Time</h3>
                <DateTimePicker
                  initialDate={new Date()}
                  minDate={new Date()}
                  availableTimeSlots={availabilitySlots}
                  onChange={handleDateTimeChange}
                />
              </div>
            </div>

            <div className="selected-time">
              {selectedTimeSlot && (
                <div>
                  <h3>Selected Time:</h3>
                  <p>
                    {selectedTimeSlot.startTime.toLocaleString()} - {selectedTimeSlot.endTime.toLocaleString()}
                  </p>
                </div>
              )}
            </div>

            <div className="action-buttons">
              <button
                className="primary-button"
                onClick={handleReschedule}
                disabled={!selectedTimeSlot || isLoading}
              >
                {isLoading ? 'Rescheduling...' : 'Confirm Reschedule'}
              </button>
              <button
                className="secondary-button"
                onClick={() => setCurrentStep(RescheduleStep.CURRENT_DETAILS)}
                disabled={isLoading}
              >
                Back
              </button>
            </div>
          </div>
        );

      case RescheduleStep.CONFIRMATION:
        if (!newMeeting) return null;

        return (
          <div className="reschedule-confirmation">
            <h2>Meeting Rescheduled</h2>
            <p>Your meeting has been successfully rescheduled.</p>
            <BookingConfirmation meeting={newMeeting} />
            <button
              className="primary-button"
              onClick={onComplete}
            >
              Done
            </button>
          </div>
        );
    }
  };

  if (error) {
    return (
      <div className="error-container">
        <h2>Error</h2>
        <p>{error}</p>
        <button onClick={onCancel}>Back</button>
      </div>
    );
  }

  return (
    <div className="reschedule-meeting-container">
      {renderContent()}
    </div>
  );
};
