          startTime: selectedTimeSlot.startTime.toISOString(),
          endTime: selectedTimeSlot.endTime.toISOString(),
          timeZone: selectedTimeSlot.timeZone,
          attendees: [meetingDetails.email],
          notes: meetingDetails.notes,
          guestName: meetingDetails.name
        })
      });

      if (!response.ok) {
        throw new Error('Failed to create meeting');
      }

      const data = await response.json();
      setCreatedMeeting({
        ...data,
        startTime: new Date(data.startTime),
        endTime: new Date(data.endTime)
      });

      setCurrentStep(ScheduleStep.CONFIRMATION);
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  // Handle input changes
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setMeetingDetails(prev => ({ ...prev, [name]: value }));
  };

  // Render based on current step
  const renderContent = () => {
    switch (currentStep) {
      case ScheduleStep.SELECT_DATE:
        return (
          <div className="select-date-container">
            <h1>Schedule a Meeting</h1>

            {connectedCalendars.length === 0 ? (
              <div className="no-calendars">
                <p>No calendars connected. Please connect a calendar to continue.</p>
                <button onClick={() => router.push(`/settings/calendars?userId=${userId}`)}>
                  Connect Calendar
                </button>
              </div>
            ) : (
              <>
                <div className="availability-section">
                  <h2>Select a Time Slot</h2>
                  <AvailabilityDisplay
                    userId={userId as string}
                    startDate={new Date()}
                    numberOfDays={7}
                    onSlotSelect={handleTimeSlotSelect}
                    slotDuration={30}
                    workingHours={{ start: '09:00', end: '17:00' }}
                  />
                </div>

                <div className="date-time-picker-section">
                  <h2>Or Choose a Custom Time</h2>
                  <DateTimePicker
                    initialDate={new Date()}
                    minDate={new Date()}
                    onChange={handleDateTimeChange}
                  />
                </div>
              </>
            )}
          </div>
        );

      case ScheduleStep.ENTER_DETAILS:
        if (!selectedTimeSlot) return null;

        return (
          <div className="enter-details-container">
            <h1>Meeting Details</h1>

            <div className="selected-time-info">
              <h3>Selected Time:</h3>
              <p>
                {selectedTimeSlot.startTime.toLocaleString(undefined, {
                  weekday: 'long',
                  year: 'numeric',
                  month: 'long',
                  day: 'numeric',
                  hour: 'numeric',
                  minute: '2-digit'
                })}
                {' - '}
                {selectedTimeSlot.endTime.toLocaleString(undefined, {
                  hour: 'numeric',
                  minute: '2-digit'
                })}
              </p>
            </div>

            <form onSubmit={handleDetailsSubmit} className="meeting-details-form">
              <div className="form-group">
                <label htmlFor="title">Meeting Title</label>
                <input
                  type="text"
                  id="title"
                  name="title"
                  value={meetingDetails.title}
                  onChange={handleInputChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="name">Your Name</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={meetingDetails.name}
                  onChange={handleInputChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="email">Your Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={meetingDetails.email}
                  onChange={handleInputChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="notes">Additional Notes</label>
                <textarea
                  id="notes"
                  name="notes"
                  value={meetingDetails.notes}
                  onChange={handleInputChange}
                  rows={4}
                />
              </div>

              <div className="form-actions">
                <button
                  type="button"
                  className="back-button"
                  onClick={() => setCurrentStep(ScheduleStep.SELECT_DATE)}
                >
                  Back
                </button>
                <button
                  type="submit"
                  className="schedule-button"
                  disabled={isLoading}
                >
                  {isLoading ? 'Scheduling...' : 'Schedule Meeting'}
                </button>
              </div>
            </form>
          </div>
        );

      case ScheduleStep.CONFIRMATION:
        if (!createdMeeting) return null;

        return (
          <div className="confirmation-container">
            <BookingConfirmation meeting={createdMeeting} />
          </div>
        );
    }
  };

  // Handle reschedule mode
  if (rescheduleId) {
    return (
      <div className="schedule-page reschedule-mode">
        <RescheduleMeeting
          meetingId={rescheduleId as string}
          userId={userId as string}
          onComplete={() => router.push('/meetings')}
          onCancel={() => router.push('/meetings')}
        />
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <h2>Error</h2>
        <p>{error}</p>
        <button onClick={() => setError(null)}>Try Again</button>
      </div>
    );
  }

  return (
    <div className="schedule-page">
      {renderContent()}
    </div>
  );
};

export default SchedulePage;
```

