import { supabase } from '../lib/supabaseClient';
import { RewardHistory, RewardStatus } from '../types/rewards';

export const getRewardHistory = async (filters: {
  company_id?: string;
  advocate_id?: string;
  status?: RewardStatus;
  start_date?: string;
  end_date?: string;
}): Promise<RewardHistory[]> => {
  let query = supabase
    .from('reward_history')
    .select(`
      *,
      reward_option:reward_option_id(*),
      fulfillment:id(reward_fulfillment(*))
    `);

  if (filters.company_id) {
    query = query.eq('reward_option.company_id', filters.company_id);
  }

  if (filters.advocate_id) {
    query = query.eq('advocate_id', filters.advocate_id);
  }

  if (filters.status) {
    query = query.eq('status', filters.status);
  }

  if (filters.start_date) {
    query = query.gte('created_at', filters.start_date);
  }

  if (filters.end_date) {
    query = query.lte('created_at', filters.end_date);
  }

  const { data, error } = await query;
  if (error) throw error;
  return data as unknown as RewardHistory[];
};

