export const updateRewardStatus = async (id: string, status: RewardStatus, processed_at?: string): Promise<RewardHistory> => {
  const updates: Partial<RewardHistory> = { 
    status, 
    updated_at: new Date().toISOString() 
  };

  if (status === 'completed' && !processed_at) {
    updates.processed_at = new Date().toISOString();
  } else if (processed_at) {
    updates.processed_at = processed_at;
