// Function to generate tax reporting data
export const generateTaxReportingData = async (year: number, company_id?: string): Promise<any[]> => {
  // Query all completed stipends for the given year
  const startDate = `${year}-01-01`;
  const endDate = `${year}-12-31`;

  let query = supabase
    .from('reward_history')
    .select(`
      id,
      advocate_id,
      amount,
      processed_at,
      reward_option:reward_option_id(*),
      advocate:advocate_id(id, first_name, last_name, email, tax_id)
    `)
    .eq('status', 'completed')
    .eq('reward_option.type', 'stipend')
    .gte('processed_at', startDate)
    .lte('processed_at', endDate);

  if (company_id) {
    query = query.eq('reward_option.company_id', company_id);
  }

  const { data, error } = await query;

  if (error) throw error;

  // Group by advocate and sum amounts
  const advocateMap = new Map();

  data.forEach(reward => {
    const advocateId = reward.advocate_id;
    if (!advocateMap.has(advocateId)) {
      advocateMap.set(advocateId, {
        advocate_id: advocateId,
        first_name: reward.advocate.first_name,
        last_name: reward.advocate.last_name,
        email: reward.advocate.email,
        tax_id: reward.advocate.tax_id,
        total_amount: 0,
        payments: []
      });
    }

    const record = advocateMap.get(advocateId);
    record.total_amount += reward.amount;
    record.payments.push({
      reward_id: reward.id,
      amount: reward.amount,
      date: reward.processed_at
    });
  });

