// Helper function to get current company credits balance for a company
export const getCompanyCreditsBalance = async (company_id: string): Promise<number> => {
  const { data, error } = await supabase
    .from('reward_history')
    .select('amount, status, reward_option:reward_option_id(*)')
    .eq('reward_option.company_id', company_id)
    .eq('status', 'completed')
    .eq('reward_option.type', 'company_credit');

  if (error) throw error;

  // Sum up all completed company credit rewards
  return data.reduce((total, reward) => total + (reward.amount || 0), 0);
