export const processCompanyCredits = async (reward: RewardHistory): Promise<RewardFulfillment> => {
  try {
    // Get company ID from the reward option
    const { data: rewardOption, error: optionError } = await supabase
      .from('reward_options')
      .select('company_id')
      .eq('id', reward.reward_option_id)
      .single();

    if (optionError) throw optionError;

    // Create fulfillment record
    const { data: fulfillment, error } = await supabase
      .from('reward_fulfillment')
      .insert({
