          company_id: rewardOption.company_id,
          notes: 'Company credits added to balance'
        }
      })
      .select()
      .single();

    if (error) throw error;

    // Update reward status to completed immediately since this is just adding to the balance
    await updateRewardStatus(reward.id, 'completed');

    return fulfillment as RewardFulfillment;
  } catch (error) {
    console.error('Error processing company credits:', error);
    await updateRewardStatus(reward.id, 'failed');
