  }
};

// Function to redeem company credits for an event or merchandise
export const redeemCompanyCredits = async (
  company_id: string,
  amount: number,
  details: {
    description: string;
    event_date?: string;
    category: 'team_event' | 'merchandise' | 'other';
    requested_by: string;
  }
): Promise<{ success: boolean; message: string; remaining_balance?: number }> => {
  // Get current balance
  const currentBalance = await getCompanyCreditsBalance(company_id);

  if (currentBalance < amount) {
    return {
      success: false,
      message: `Insufficient credits. Current balance: $${currentBalance.toFixed(2)}`
    };
  }

  // Create redemption record
  const { data, error } = await supabase
    .from('company_credit_redemptions')
