      event_date: details.event_date,
      category: details.category,
      requested_by: details.requested_by,
      status: 'pending'
    })
    .select()
    .single();

  if (error) {
    console.error('Error creating redemption record:', error);
    return {
      success: false,
      message: 'Failed to create redemption request'
    };
  }

  return {
    success: true,
    message: 'Redemption request submitted successfully',
    remaining_balance: currentBalance - amount
  };
