import { createRewardRecord, updateRewardStatus } from './rewardHistoryService';
import { processCharitableDonation } from './rewardProcessors/charitableDonationProcessor';
import { processDevelopmentFund } from './rewardProcessors/developmentFundProcessor';
import { processStipend } from './rewardProcessors/stipendProcessor';
import { processCompanyCredits } from './rewardProcessors/companyCreditsProcessor';

// Get available rewards for a company
export const getAvailableRewards = async (companyId: string) => {
  const { data, error } = await supabase
    .from('reward_options')
    .select('*')
    .eq('company_id', companyId)
    .eq('active', true);

  if (error) throw error;
  return data as RewardOption[];
};

// Process a reward based on its type
export const processReward = async (
  advocateId: string,
  rewardOptionId: string,
  callId?: string
): Promise<RewardHistory> => {
  try {
    // Get the reward option
    const { data: rewardOption, error: optionError } = await supabase
      .from('reward_options')
      .select('*')
      .eq('id', rewardOptionId)
      .single();

    if (optionError) throw optionError;

    // Create reward record
    const reward = await createRewardRecord(
      advocateId,
      rewardOptionId,
      rewardOption.amount,
      callId
    );

    // Process based on reward type
    let fulfillment: RewardFulfillment;

    switch (rewardOption.type) {
      case 'charity':
        fulfillment = await processCharitableDonation(reward);
        break;
      case 'development_fund':
        fulfillment = await processDevelopmentFund(reward);
        break;
      case 'stipend':
        fulfillment = await processStipend(reward);
        break;
      case 'company_credit':
        fulfillment = await processCompanyCredits(reward);
        break;
      default:
        throw new Error(`Unsupported reward type: ${rewardOption.type}`);
    }

    // Get the updated reward with fulfillment details
    const updatedReward = await getRewardWithFulfillment(reward.id);
    return updatedReward;
  } catch (error) {
    console.error('Error processing reward:', error);
    throw error;
  }
};

// Helper to get a reward with its fulfillment details
export const getRewardWithFulfillment = async (rewardId: string): Promise<RewardHistory> => {
  const { data, error } = await supabase
    .from('reward_history')
    .select(`
      *,
      reward_option:reward_option_id(*),
      fulfillment:id(reward_fulfillment(*))
    `)
    .eq('id', rewardId)
    .single();

  if (error) throw error;
  return data as unknown as RewardHistory;
};

// Cancel a pending reward
export const cancelReward = async (rewardId: string, reason: string): Promise<RewardHistory> => {
  // Get the current reward
  const { data: reward, error: fetchError } = await supabase
    .from('reward_history')
    .select('*')
    .eq('id', rewardId)
    .single();

  if (fetchError) throw fetchError;

  // Only allow cancellation of pending or processing rewards
  if (!['pending', 'processing'].includes(reward.status)) {
    throw new Error(`Cannot cancel reward with status: ${reward.status}`);
  }

  // Update the reward status
  const { data: updatedReward, error: updateError } = await supabase
    .from('reward_history')
    .update({
      status: 'failed',
      updated_at: new Date().toISOString()
    })
    .eq('id', rewardId)
    .select()
    .single();

  if (updateError) throw updateError;

  // Update the fulfillment record if it exists
  const { data: fulfillment } = await supabase
    .from('reward_fulfillment')
    .select('id')
    .eq('reward_id', rewardId)
    .single();

  if (fulfillment) {
    await supabase
      .from('reward_fulfillment')
      .update({
        status: 'cancelled',
        details: {
          ...fulfillment.details,
          cancellation_reason: reason,
          cancelled_at: new Date().toISOString()
        }
      })
      .eq('id', fulfillment.id);
  }

  return updatedReward as RewardHistory;
};
