// Generate tax documents for a specific year
export const generateTaxDocuments = async (year: number, company_id?: string): Promise<TaxDocument[]> => {
  try {
    // Get tax reporting data for the year
    const taxData = await generateTaxReportingData(year, company_id);

    // Filter for advocates who received more than $600 (IRS threshold for 1099-MISC)
    const eligibleAdvocates = taxData.filter(data => data.total_amount >= 600);

    // Create tax document records
    const documents: TaxDocument[] = [];

    for (const advocate of eligibleAdvocates) {
      // Check if document already exists
      const { data: existingDoc } = await supabase
        .from('tax_documents')
        .select('id')
        .eq('advocate_id', advocate.advocate_id)
        .eq('year', year)
        .single();

      if (existingDoc) {
        // Update existing document
        const { data: updatedDoc } = await supabase
          .from('tax_documents')
          .update({
            total_amount: advocate.total_amount,
            updated_at: new Date().toISOString()
          })
          .eq('id', existingDoc.id)
          .select()
          .single();

        documents.push(updatedDoc as TaxDocument);
      } else {
        // Create new document
        const { data: newDoc } = await supabase
          .from('tax_documents')
          .insert({
            advocate_id: advocate.advocate_id,
            year,
            document_type: '1099-MISC',
            status: 'pending',
            total_amount: advocate.total_amount
          })
          .select()
          .single();

        documents.push(newDoc as TaxDocument);
      }
    }

