// Generate a summary report of rewards
export const generateRewardSummary = async (filters: {
  company_id?: string;
  start_date?: string;
  end_date?: string;
}): Promise<RewardSummary> => {
  let query = supabase
    .from('reward_history')
    .select(`
      *,
      reward_option:reward_option_id(*)
    `);

  if (filters.company_id) {
    query = query.eq('reward_option.company_id', filters.company_id);
  }

  if (filters.start_date) {
    query = query.gte('created_at', filters.start_date);
  }

  if (filters.end_date) {
    query = query.lte('created_at', filters.end_date);
  }

  const { data, error } = await query;

  if (error) throw error;

  // Initialize summary
  const summary: RewardSummary = {
    total_rewards: 0,
    total_amount: 0,
    by_status: {},
    by_type: {}
  };

  // Process data
  data.forEach(reward => {
    summary.total_rewards++;
    summary.total_amount += reward.amount;

    // By status
    if (!summary.by_status[reward.status]) {
      summary.by_status[reward.status] = { count: 0, amount: 0 };
    }
    summary.by_status[reward.status].count++;
    summary.by_status[reward.status].amount += reward.amount;

    // By type
    const type = reward.reward_option.type;
    if (!summary.by_type[type]) {
      summary.by_type[type] = { count: 0, amount: 0 };
    }
    summary.by_type[type].count++;
    summary.by_type[type].amount += reward.amount;
  });

  return summary;
};

