const AdvocateRewardHistory: React.FC<AdvocateRewardHistoryProps> = ({ advocateId: propAdvocateId }) => {
  const { advocateId: urlAdvocateId } = useParams<{ advocateId: string }>();
  const advocateId = propAdvocateId || urlAdvocateId;

  const [rewards, setRewards] = useState<RewardHistory[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [filter, setFilter] = useState<{
    status: string | null;
    type: RewardType | null;
    dateRange: 'all' | '30days' | '90days' | 'year';
  }>({ status: null, type: null, dateRange: 'all' });

  useEffect(() => {
    if (!advocateId) return;

    const fetchRewards = async () => {
      setLoading(true);
      setError(null);

      try {
        let query = supabase
          .from('reward_history')
          .select(`
            *,
            reward_option:reward_option_id(*),
            fulfillment:id(reward_fulfillment(*))
          `)
          .eq('advocate_id', advocateId)
          .order('created_at', { ascending: false });

        // Apply filters
        if (filter.status) {
          query = query.eq('status', filter.status);
        }

        if (filter.type) {
          query = query.eq('reward_option.type', filter.type);
        }

        if (filter.dateRange !== 'all') {
          const now = new Date();
          let startDate: Date;

          switch (filter.dateRange) {
            case '30days':
              startDate = new Date(now.setDate(now.getDate() - 30));
              break;
            case '90days':
              startDate = new Date(now.setDate(now.getDate() - 90));
              break;
            case 'year':
              startDate = new Date(now.setFullYear(now.getFullYear() - 1));
              break;
            default:
              startDate = new Date(0); // Beginning of time
          }

          query = query.gte('created_at', startDate.toISOString());
        }

        const { data, error } = await query;

        if (error) throw error;
        setRewards(data as unknown as RewardHistory[]);
      } catch (err) {
