  approver_id?: string;
  notes?: string;
  created_at: string;
  updated_at: string;
}

// Check if a reward requires compliance approval
export const checkComplianceRequirements = async (reward: RewardHistory): Promise<{
  requiresApproval: boolean;
  checks: Partial<ComplianceCheck>[];
}> => {
  const checks: Partial<ComplianceCheck>[] = [];

  // Get company compliance settings
  const { data: rewardOption } = await supabase
    .from('reward_options')
    .select('*, company:company_id(settings)')
    .eq('id', reward.reward_option_id)
    .single();

  const companySettings = rewardOption.company.settings || {};
  const complianceSettings = companySettings.compliance || {
    value_threshold: 500, // Default $500 threshold for high-value rewards
    monthly_limit: 2000,  // Default $2000 monthly limit per advocate
    require_tax_approval: true
  };

  // Check 1: High-value reward check
  if (reward.amount >= complianceSettings.value_threshold) {
    checks.push({
      reward_id: reward.id,
      check_type: 'value_threshold',
      status: 'pending',
      notes: `Reward amount (${reward.amount}) exceeds threshold (${complianceSettings.value_threshold})`
    });
  }

  // Check 2: Tax implications check
  if (complianceSettings.require_tax_approval && rewardOption.tax_implications) {
    checks.push({
      reward_id: reward.id,
      check_type: 'tax_implications',
      status: 'pending',
      notes: 'Reward has tax implications requiring approval'
    });
  }

  // Check 3: Monthly frequency/limit check
  const startOfMonth = new Date();
  startOfMonth.setDate(1);
  startOfMonth.setHours(0, 0, 0, 0);

  const { data: monthlyRewards } = await supabase
    .from('reward_history')
    .select('amount')
    .eq('advocate_id', reward.advocate_id)
    .gte('created_at', startOfMonth.toISOString())
    .not('status', 'eq', 'failed');

  const monthlyTotal = monthlyRewards.reduce((sum, r) => sum + r.amount, 0) + reward.amount;

  if (monthlyTotal > complianceSettings.monthly_limit) {
    checks.push({
      reward_id: reward.id,
      check_type: 'frequency',
      status: 'pending',
      notes: `Monthly total (${monthlyTotal}) exceeds limit (${complianceSettings.monthly_limit})`
    });
  }

  return {
    requiresApproval: checks.length > 0,
    checks
  };
};

// Create compliance checks for a reward
export const createComplianceChecks = async (reward: RewardHistory): Promise<ComplianceCheck[]> => {
  const { requiresApproval, checks } = await checkComplianceRequirements(reward);

  if (!requiresApproval) {
    return [];
  }

  // Insert all checks
  const { data, error } = await supabase
    .from('compliance_checks')
    .insert(checks)
    .select();

  if (error) throw error;

  // Update reward status to indicate pending approval
  await updateRewardStatus(reward.id, 'pending');

  return data as ComplianceCheck[];
};

// Get pending compliance checks
export const getPendingComplianceChecks = async (companyId?: string): Promise<{
  reward: RewardHistory;
  checks: ComplianceCheck[];
}[]> => {
  let query = supabase
    .from('compliance_checks')
    .select(`
      *,
      reward:reward_id(*, reward_option:reward_option_id(*))
    `)
    .eq('status', 'pending');

  if (companyId) {
    query = query.eq('reward.reward_option.company_id', companyId);
  }

  const { data, error } = await query;

  if (error) throw error;

  // Group checks by reward
  const rewardMap = new Map<string, {
    reward: RewardHistory;
    checks: ComplianceCheck[];
  }>();

  data.forEach(check => {
    const rewardId = check.reward_id;
    if (!rewardMap.has(rewardId)) {
      rewardMap.set(rewardId, {
        reward: check.reward,
        checks: []
      });
    }

    rewardMap.get(rewardId)!.checks.push(check);
  });

  return Array.from(rewardMap.values());
};

// Approve or reject a compliance check
export const resolveComplianceCheck = async (
  checkId: string,
  resolution: {
    status: 'approved' | 'rejected';
    approver_id: string;
    notes?: string;
  }
): Promise<ComplianceCheck> => {
  // Update the check
  const { data: updatedCheck, error } = await supabase
    .from('compliance_checks')
    .update({
      status: resolution.status,
      approver_id: resolution.approver_id,
      notes: resolution.notes,
      updated_at: new Date().toISOString()
    })
    .eq('id', checkId)
    .select('*, reward:reward_id(*)')
    .single();

  if (error) throw error;

  // Check if all checks for this reward are resolved
  const { data: remainingChecks } = await supabase
    .from('compliance_checks')
    .select('status')
    .eq('reward_id', updatedCheck.reward_id)
    .eq('status', 'pending');

  // If no pending checks remain, update the reward status
  if (remainingChecks.length === 0) {
    // Check if any were rejected
    const { data: rejectedChecks } = await supabase
      .from('compliance_checks')
      .select('id')
      .eq('reward_id', updatedCheck.reward_id)
      .eq('status', 'rejected');

    if (rejectedChecks.length > 0) {
      // At least one check was rejected, fail the reward
      await updateRewardStatus(updatedCheck.reward_id, 'failed');
    } else {
      // All checks approved, continue processing
      await updateRewardStatus(updatedCheck.reward_id, 'processing');

      // In a real implementation, you would trigger the reward processing here
      // For example: await processReward(updatedCheck.reward);
    }
  }

  return updatedCheck as ComplianceCheck;
};
