// services/apiKeyService.ts
export const generateApiKey = async (clientName: string, tenantId: string, scopes: string[]) => {
  // Generate random key with sufficient entropy
  const key = crypto.randomBytes(32).toString('hex')

  // Hash for storage
  const hashedKey = await bcrypt.hash(key, 10)

  // Store in database
  const apiKey = await prisma.apiKey.create({
    data: {
      key: hashedKey,
      client_name: clientName,
      tenant_id: tenantId,
      scopes,
      expires_at: new Date(Date.now() + 365 * 24 * 60 * 60 * 1000), // 1 year expiry
      status: 'active'
    }
  })

  return { originalKey: key, apiKey }
}
