1. Refine and expand the existing middleware to handle both authentication methods:
```typescript
// middleware/apiMiddleware.ts
export function withApiAuth(handler) {
  return async (req, res) => {
    // Check for API key authentication
    const apiKey = req.headers['x-api-key']

    // Check for OAuth token authentication
    const authHeader = req.headers['authorization']
    const bearerToken = authHeader?.startsWith('Bearer ') ? authHeader.substring(7) : null

    if (!apiKey && !bearerToken) {
      return res.status(401).json({ error: 'Authentication required' })
    }

    try {
      if (apiKey) {
        // Validate API key
        const apiKeyData = await validateApiKey(apiKey)
        if (!apiKeyData) {
          return res.status(403).json({ error: 'Invalid API key' })
        }
        req.auth = {
          type: 'apiKey',
          clientId: apiKeyData.client_name,
          tenantId: apiKeyData.tenant_id,
          scopes: apiKeyData.scopes
        }
      } else if (bearerToken) {
        // Validate OAuth token
        const tokenData = await validateOAuthToken(bearerToken)
        if (!tokenData) {
          return res.status(403).json({ error: 'Invalid token' })
        }
        req.auth = {
          type: 'oauth',
          clientId: tokenData.client_id,
          tenantId: tokenData.tenant_id,
          userId: tokenData.user_id,
          scopes: tokenData.scopes
        }
      }

      // Continue to handler
      return handler(req, res)
    } catch (error) {
      console.error('Authentication error:', error)
      return res.status(500).json({ error: 'Authentication error' })
    }
  }
}
