2. Implement rate limiting middleware using a sliding window algorithm:
```typescript
// middleware/rateLimitMiddleware.ts
import { Redis } from 'ioredis'
import { NextApiRequest, NextApiResponse } from 'next'

const redis = new Redis(process.env.REDIS_URL)

export function withRateLimit(options: {
  windowMs: number,
  max: number,
  keyGenerator?: (req: NextApiRequest) => string
}) {
  return (handler) => {
    return async (req: NextApiRequest, res: NextApiResponse) => {
      // Generate rate limit key based on client ID or IP
      const key = options.keyGenerator
        ? options.keyGenerator(req)
        : `ratelimit:${req.auth?.clientId || req.socket.remoteAddress}:${req.method}:${req.url}`

      // Get current count
      const now = Date.now()
      const windowStart = now - options.windowMs

      // Add current request timestamp to the window
      await redis.zadd(key, now, `${now}`)

      // Remove old entries outside the current window
      await redis.zremrangebyscore(key, 0, windowStart)

      // Set expiry on the set
      await redis.expire(key, Math.ceil(options.windowMs / 1000))

      // Count requests in the current window
      const count = await redis.zcard(key)

      // Set rate limit headers
      res.setHeader('X-RateLimit-Limit', options.max)
      res.setHeader('X-RateLimit-Remaining', Math.max(0, options.max - count))

      // If over limit, return 429
      if (count > options.max) {
        return res.status(429).json({
          error: 'Too many requests',
          retryAfter: Math.ceil(options.windowMs / 1000)
        })
      }

      // Continue to handler
      return handler(req, res)
    }
  }
}
```
