```typescript
// pages/api/v1/analytics/metrics.ts
import { withApiAuth } from '@/middleware/apiMiddleware'
import { withRateLimit } from '@/middleware/rateLimitMiddleware'
import { prisma } from '@/lib/prisma'

const handler = async (req, res) => {
  if (req.method === 'GET') {
    try {
      // Extract query parameters
      const { period = '30d' } = req.query

      // Calculate date range based on period
      const now = new Date()
      let startDate = new Date()

      switch (period) {
        case '7d':
          startDate.setDate(now.getDate() - 7)
          break
        case '30d':
          startDate.setDate(now.getDate() - 30)
          break
        case '90d':
          startDate.setDate(now.getDate() - 90)
          break
        case 'ytd':
          startDate = new Date(now.getFullYear(), 0, 1) // January 1st of current year
          break
        default:
          startDate.setDate(now.getDate() - 30) // Default to 30 days
      }

      // Run queries in parallel
      const [callsCompleted, callsScheduled, advocatesActive, opportunitiesCreated] = await Promise.all([
        // Count completed calls in period
        prisma.referenceCall.count({
          where: {
            tenant_id: req.auth.tenantId,
            status: 'completed',
            scheduled_at: {
              gte: startDate,
              lte: now
            }
          }
        }),

        // Count scheduled calls (future)
        prisma.referenceCall.count({
          where: {
            tenant_id: req.auth.tenantId,
            status: 'scheduled',
            scheduled_at: {
              gte: now
            }
          }
        }),

        // Count active advocates
        prisma.advocate.count({
          where: {
            tenant_id: req.auth.tenantId,
            status: 'active'
          }
        }),

        // Count opportunities created in period
        prisma.opportunity.count({
          where: {
            tenant_id: req.auth.tenantId,
            created_at: {
              gte: startDate,
              lte: now
            }
          }
        })
      ])

      // Return metrics
      return res.status(200).json({
        period,
        start_date: startDate.toISOString(),
        end_date: now.toISOString(),
        metrics: {
          calls_completed: callsCompleted,
          calls_scheduled: callsScheduled,
          advocates_active: advocatesActive,
          opportunities_created: opportunitiesCreated
        }
      })
    } catch (error) {
      console.error('Error fetching metrics:', error)
      return res.status(500).json({ error: 'Failed to fetch metrics' })
    }
  } else {
    res.setHeader('Allow', ['GET'])
    return res.status(405).json({ error: 'Method not allowed' })
  }
}

export default withRateLimit({
  windowMs: 60 * 1000, // 1 minute
  max: 30 // Lower limit for analytics endpoints
})(withApiAuth(handler))
