```typescript
// services/webhookService.ts
import { prisma } from '@/lib/prisma'

export interface WebhookPayload {
  event: string
  data: any
  tenant_id: string
  timestamp: string
}

export const registerWebhook = async ({
  tenantId,
  url,
  secret,
  events,
  description = ''
}: {
  tenantId: string
  url: string
  secret: string
  events: string[]
  description?: string
}) => {
  return prisma.webhook.create({
    data: {
      tenant_id: tenantId,
      url,
      secret,
      events,
      description,
      status: 'active',
      created_at: new Date()
    }
  })
}

export const getWebhooksForEvent = async (event: string, tenantId: string) => {
  return prisma.webhook.findMany({
    where: {
      tenant_id: tenantId,
      status: 'active',
      events: {
        has: event
      }
    }
  })
}

export const deliverWebhook = async (webhook, payload: WebhookPayload) => {
  try {
    // Create signature for webhook verification
    const signature = createHmacSignature(webhook.secret, JSON.stringify(payload))

    // Send webhook with retry logic
    const response = await fetch(webhook.url, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'X-Webhook-Signature': signature
      },
      body: JSON.stringify(payload)
    })

    // Log delivery attempt
    await prisma.webhookDelivery.create({
      data: {
        webhook_id: webhook.id,
        event: payload.event,
        payload: payload,
        status_code: response.status,
        success: response.ok,
        delivered_at: new Date()
      }
    })

    return response.ok
  } catch (error) {
    // Log failed delivery
    await prisma.webhookDelivery.create({
      data: {
        webhook_id: webhook.id,
        event: payload.event,
        payload: payload,
        status_code: 0,
        success: false,
        error_message: error.message,
        delivered_at: new Date()
      }
    })

    return false
  }
}

function createHmacSignature(secret: string, payload: string) {
  const crypto = require('crypto')
  return crypto.createHmac('sha256', secret).update(payload).digest('hex')
}
```

2. Create webhook trigger functions for different events:
```typescript
// services/webhookService.ts (continued)
export const triggerWebhook = async (event: string, data: any, tenantId: string) => {
  // Get webhooks subscribed to this event
  const webhooks = await getWebhooksForEvent(event, tenantId)

  if (webhooks.length === 0) {
    return
  }

  // Prepare payload
  const payload: WebhookPayload = {
    event,
    data,
    tenant_id: tenantId,
    timestamp: new Date().toISOString()
  }

  // Deliver to all webhooks in parallel
  await Promise.allSettled(
    webhooks.map(webhook => deliverWebhook(webhook, payload))
  )
}
