  method,
  statusCode,
  responseTime
}: {
  clientId: string
  tenantId: string
  endpoint: string
  method: string
  statusCode: number
  responseTime: number
}) => {
  // Store hourly aggregated data
  const date = new Date()
  const hour = date.getHours()
  const day = date.getDate()
  const month = date.getMonth() + 1
  const year = date.getFullYear()

  // Create unique key for this hour
  const hourlyKey = `${year}-${month.toString().padStart(2, '0')}-${day.toString().padStart(2, '0')}-${hour.toString().padStart(2, '0')}`

  // Update or create hourly stats
  await prisma.apiUsageStat.upsert({
    where: {
      tenant_id_client_id_endpoint_method_hourly_key: {
        tenant_id: tenantId,
        client_id: clientId,
        endpoint,
        method,
        hourly_key: hourlyKey
      }
    },
    update: {
      request_count: { increment: 1 },
      error_count: statusCode >= 400 ? { increment: 1 } : undefined,
      total_response_time: { increment: responseTime }
    },
    create: {
      tenant_id: tenantId,
      client_id: clientId,
      endpoint,
      method,
      hourly_key: hourlyKey,
      request_count: 1,
      error_count: statusCode >= 400 ? 1 : 0,
      total_response_time: responseTime,
      year,
      month,
      day,
      hour
    }
  })
}

export const getApiUsage = async ({
  tenantId,
  clientId,
  startDate,
  endDate
}: {
  tenantId: string
  clientId?: string
  startDate: Date
  endDate: Date
}) => {
  // Query aggregated usage data
  const where = {
    tenant_id: tenantId,
    ...(clientId ? { client_id: clientId } : {}),
    // Convert to date range condition
    OR: [
      {
        year: { gt: startDate.getFullYear() },
        year: { lt: endDate.getFullYear() }
      },
      {
        year: startDate.getFullYear(),
        month: { gte: startDate.getMonth() + 1 }
      },
      {
        year: endDate.getFullYear(),
        month: { lte: endDate.getMonth() + 1 }
      }
    ]
  }

  return prisma.apiUsageStat.findMany({
    where,
    orderBy: [
      { year: 'asc' },
      { month: 'asc' },
      { day: 'asc' },
      { hour: 'asc' }
    ]
  })
}
