// middleware/apiTrackingMiddleware.ts
import { trackApiRequest } from '@/services/apiUsageService'

export function withApiTracking(handler) {
  return async (req, res) => {
    const startTime = Date.now()

    // Create a proxy of the res object to capture the status code
    const originalEnd = res.end
    let statusCode = 200

    res.end = function(chunk, encoding) {
      // Capture status code before ending response
      statusCode = res.statusCode

      // Track API request asynchronously (don't await)
      trackApiRequest({
        clientId: req.auth?.clientId || 'anonymous',
        tenantId: req.auth?.tenantId || 'anonymous',
        endpoint: req.url.split('?')[0], // Remove query params
        method: req.method,
        statusCode,
        responseTime: Date.now() - startTime
      }).catch(err => console.error('Error tracking API usage:', err))

      // Call original end method
      return originalEnd.call(this, chunk, encoding)
    }

    // Continue to handler
    return handler(req, res)
  }
}
```

