2. Create version routing middleware:
```typescript
// middleware/versionMiddleware.ts
import { NextApiRequest, NextApiResponse } from 'next'

export function withVersionRouting(handlers: {
  v1?: (req: NextApiRequest, res: NextApiResponse) => Promise<void>
  v2?: (req: NextApiRequest, res: NextApiResponse) => Promise<void>
}) {
  return async (req: NextApiRequest, res: NextApiResponse) => {
    // Extract version from URL path
    const path = req.url.split('?')[0]
    const versionMatch = path.match(/\/api\/(v\d+)\//)
    const version = versionMatch ? versionMatch[1] : 'v1' // Default to v1

    // Check if handler exists for this version
    if (handlers[version]) {
      return handlers[version](req, res)
    }

    // If no handler for this version, return 404
    return res.status(404).json({
      error: 'API version not found',
      message: `The requested API version '${version}' is not supported.`
    })
  }
}
```

