2. Create error handling middleware:
```typescript
// middleware/errorHandlingMiddleware.ts
import { ApiError, ServerError } from '@/lib/errors'

export function withErrorHandling(handler) {
  return async (req, res) => {
    try {
      return await handler(req, res)
    } catch (error) {
      console.error('API Error:', error)

      // If it's already an ApiError, use it directly
      if (error instanceof ApiError) {
        return res.status(error.statusCode).json(error.toJSON())
      }

      // For validation errors from a validation library
      if (error.name === 'ValidationError') {
        return res.status(400).json({
          error: {
            code: 'VALIDATION_ERROR',
            message: 'Validation failed',
            details: error.details || error.errors || error.message
          }
        })
      }

      // For database unique constraint errors
      if (error.code === 'P2002') { // Prisma unique constraint error
        return res.status(409).json({
          error: {
            code: 'CONFLICT',
            message: 'Resource already exists',
            details: {
              fields: error.meta?.target || []
            }
          }
        })
      }

      // For unknown errors, return a generic server error
      const serverError = new ServerError()
      return res.status(serverError.statusCode).json(serverError.toJSON())
    }
  }
}
```

