Enhanced implementation:
```typescript
// middleware/cacheMiddleware.ts
import { createClient } from 'redis';

const redisClient = createClient({
  url: process.env.REDIS_URL
});

redisClient.connect().catch(console.error);

export function withCaching(options = { ttlSeconds: 60, varyByHeaders: [] }) {
  return (handler) => {
    return async (req, res) => {
      // Only cache GET requests
      if (req.method !== 'GET') {
        return handler(req, res);
      }

      // Generate cache key with more context
      const cacheKey = generateCacheKey(req, options.varyByHeaders);

      // Try to get from cache
      const cachedResponse = await getFromCache(cacheKey);

      if (cachedResponse) {
        // Add cache header
        res.setHeader('X-Cache', 'HIT');
        return res.status(200).json(JSON.parse(cachedResponse));
      }

      // Add cache miss header
      res.setHeader('X-Cache', 'MISS');

      // Create response wrapper to capture data
      const originalJson = res.json;
      res.json = function(data) {
        // Only cache successful responses
        if (res.statusCode === 200) {
          setInCache(cacheKey, JSON.stringify(data), options.ttlSeconds);
        }
        return originalJson.call(res, data);
      };

      // Continue to handler
      return handler(req, res);
    };
  };
}

function generateCacheKey(req, varyByHeaders = []) {
  const url = req.url;
  const queryString = JSON.stringify(req.query);
  const headerValues = varyByHeaders.map(header => req.headers[header] || '');

  return `api:${url}:${queryString}:${headerValues.join(':')}`;
}

async function getFromCache(key) {
  return await redisClient.get(key);
}

async function setInCache(key, value, ttlSeconds) {
  await redisClient.set(key, value, { EX: ttlSeconds });
}
```

