```typescript
// middleware/rateLimitMiddleware.ts
import { createClient } from 'redis';
import { RateLimiterRedis } from 'rate-limiter-flexible';

const redisClient = createClient({
  url: process.env.REDIS_URL
});

redisClient.connect().catch(console.error);

const rateLimiter = new RateLimiterRedis({
  storeClient: redisClient,
  keyPrefix: 'ratelimit',
  points: 100, // Number of points
  duration: 60, // Per 60 seconds
});

export function withRateLimit(customPoints) {
  return async (req, res, next) => {
    try {
      // Use IP or user ID as identifier
      const identifier = req.user?.id || req.ip;

      // Use custom points if provided
      const points = customPoints || 1;

      // Check rate limit
      const rateLimiterRes = await rateLimiter.consume(identifier, points);

      // Set rate limit headers
      res.setHeader('X-RateLimit-Limit', rateLimiter.points);
      res.setHeader('X-RateLimit-Remaining', rateLimiterRes.remainingPoints);
      res.setHeader('X-RateLimit-Reset', new Date(Date.now() + rateLimiterRes.msBeforeNext).toISOString());

      next();
    } catch (error) {
      if (error.remainingPoints !== undefined) {
        // Rate limit exceeded
        res.setHeader('Retry-After', Math.round(error.msBeforeNext / 1000));
        res.status(429).json({ error: 'Too Many Requests' });
      } else {
        // Other error
        res.status(500).json({ error: 'Internal Server Error' });
      }
    }
  };
}
```

