// services/cacheService.ts
import { createClient } from 'redis';

const redisClient = createClient({
  url: process.env.REDIS_URL
});

redisClient.connect().catch(console.error);

export class CacheService {
  async get(key, region = 'default') {
    const fullKey = `${region}:${key}`;
    const data = await redisClient.get(fullKey);
    return data ? JSON.parse(data) : null;
  }

  async set(key, value, options = { ttl: 3600, region: 'default' }) {
    const { ttl, region } = options;
    const fullKey = `${region}:${key}`;
    await redisClient.set(fullKey, JSON.stringify(value), { EX: ttl });
  }

  async delete(key, region = 'default') {
    const fullKey = `${region}:${key}`;
    await redisClient.del(fullKey);
  }

  async invalidateRegion(region) {
    // Get all keys in region
    const keys = await redisClient.keys(`${region}:*`);
    if (keys.length > 0) {
      await redisClient.del(keys);
    }
  }
}

export const cacheService = new CacheService();
```

